'use strict';

import url from 'url';

function querystring(targetUrl, name) {
	name = name.replace(/[\[]/, '\\[').replace(/[\]]/, '\\]');

    let mySearch = `?${targetUrl.split('?')[1]}`,
        myRegex = new RegExp(`[\\?&]${name}=([^&#]*)`),
        myResult = myRegex.exec(mySearch);

    return !!myResult ? decodeURIComponent(myResult[1].replace(/\+/g, ' ')) : null;
};
